var rl = require('./index');

rl.oneline('./testfile', 1, function(err, res) {
  if (err) console.error(err) //handling error
  console.log("oneline", res) //print content
})

rl.multilines('./testfile', [1, 2], function(err, res) {
  if (err) console.error(err) //handling error
  console.log("multilines", res) //print content
})

rl.alllines('./testfile', function(err, res) {
  if (err) console.error(err) //handling error
  console.log("alllines", res) //print content
})